function renderOption(name, details, icon) {
  return (
    <div className="option">
      <img loading="lazy" src={icon} className="icon" />
      <div className="name">{name}</div>
      <div className="details">{details}</div>
    </div>
  );
}

function renderDeliveryOption(name, details, icon) {
  return (
    <div className="delivery-option">
      <img loading="lazy" src={icon} className="icon" />
      <div className="name">{name}</div>
      <div className="details">{details}</div>
    </div>
  );
}

function renderDivider(className = "") {
  return <div className={`divider ${className}`} />;
}
